import React, { useState } from 'react';

const ProductModal = ({ selectedProduct, onClose }) => {
    const [selectedFreshness, setSelectedFreshness] = useState(null);

    const handleSelectFreshness = () => {
        // Логика выбора свежести
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h3>{selectedProduct.name}</h3>
                <p><strong>Состав:</strong> {selectedProduct.composition}</p>
                <button onClick={handleSelectFreshness}>Выбрать свежесть</button>

                {selectedFreshness && (
                    <div>
                        <h4>Список возможных свежестей:</h4>
                        <ul>
                            {selectedFreshness.map((freshness, index) => (
                                <li key={index}>{freshness} <button>Добавить в корзину</button></li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ProductModal;
